import React, { useState,useEffect } from 'react';
import {Link,useLocation} from "react-router-dom";
import '../styles/Navbar.css';
import ReorderIcon from '@mui/icons-material/Reorder';

const Navbar = () => {
  const [expandNav,setExpandNav] = useState(false);

  const location = useLocation();

  useEffect(() => {
    setExpandNav(false);
  }, [location]);

  const activeTab = ( path) => {
    if (location.pathname === path) {
      return { textDecoration: 'underline',
        textDecorationColor: 'whitesmoke',
        textUnderlineOffset: '10px' };
    }
  };

  return (
    <div className='navBg' id={expandNav ? "open" : "close"}>
    <div className="toggleButton">
        <button
          onClick={() => {
            setExpandNav((prev) => !prev);
          }}
        >
          <ReorderIcon />
        </button>
      </div>
      <div className='links'>
        <Link style={activeTab(history, "/")} className='link' to="/">Home</Link>
        <Link style={activeTab(history, "/experience")} className='link' to="/experience">Experience</Link>
        <Link style={activeTab(history, "/projects")} className='link' to="/projects">Projects</Link>
        <Link style={activeTab(history, "/contact")} className='link' to="/contact">Contact</Link>
      </div>
    </div>
  )
}

export default Navbar;
