import {React,useState, useEffect, useRef} from 'react';
import '../styles/Home.css';
import Typed from "typed.js";
import Button from 'react-bootstrap/Button';
import Skills from '../components/Skills';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';

const Home = () => {
  const [scrollTop,setScrollTop] = useState(false)
  const el = useRef(null);

  useEffect(() => {
    const typed = new Typed(el.current, {
      strings: ["Front End Developer", "Back End Developer", "Software Engineer"],
      startDelay: 200,
      typeSpeed: 50,
      backSpeed: 70,
      backDelay: 100,
      loop: true,
      showCursor: true,
      smartBackspace: true
    });

    // Destropying
    return () => {
      typed.destroy();
    };

    if(window.scrollY>100){
      setScrollTop(true)
    }

  }, []);

  return (
    <div className='home'>
      <div id='stars'></div>
      <div id='stars2'></div>
      <div id='stars3'></div>
      <div className='container'>
        <h1 className='intro'>Hello, My Name is Dilpreet</h1>
        <p class="introSlide">
            I am a <span ref={el}></span>
        </p>
        <h2 className='intro3'>Based in New Delhi, India</h2>
        <a href='#skills'>
        <Button variant="primary" style={{marginTop: '2rem'}} >
          <div className='skillButton'>
            <span className='skillText'>See My Skills</span>
            <ArrowDownwardIcon />
          </div>
        </Button>
        </a>
      </div>
      <Skills />
      <span id="skills" />
      <a href="#" className='scrollTop'>
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM377 271c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0l-87-87-87 87c-9.4 9.4-24.6 9.4-33.9 0s-9.4-24.6 0-33.9L239 167c9.4-9.4 24.6-9.4 33.9 0L377 271z"/></svg></a>
    </div>
  )
}

export default Home;
